{"nbformat":4,"nbformat_minor":0,"metadata":{"colab":{"provenance":[],"authorship_tag":"ABX9TyNpFBk1EL+Q5UPk4H2g5Y/2"},"kernelspec":{"name":"python3","display_name":"Python 3"},"language_info":{"name":"python"}},"cells":[{"cell_type":"code","execution_count":null,"metadata":{"id":"EVbhoEsYh9ZR"},"outputs":[],"source":["from flask import Flask, render_template, send_file, request, jsonify\n","import os\n","from RunModel import ChangeAudioLanguage\n","from werkzeug.utils import secure_filename\n","app = Flask(__name__)\n","app.config['UPLOAD_FOLDER'] = 'uploads'\n","os.makedirs(app.config['UPLOAD_FOLDER'], exist_ok=True)\n","flag = False\n","@app.route('/')\n","def index():\n","    # Render the HTML template with the audio player\n","    return render_template('index1.html')\n","@app.route('/upload', methods=['POST'])\n","def upload_audio():\n","    try:\n","        flag = False\n","        audio_file = request.files.get('audio')\n","        filename = request.form.get('filename', 'recording.wav')\n","        filename = secure_filename(filename)\n","        fr = request.form.get('fr')\n","        to = request.form.get('to')\n","        print(fr,to)\n","        file_path = os.path.join(app.config['UPLOAD_FOLDER'], filename)\n","        # Save the uploaded file to the server\n","        audio_file.save(file_path)\n","        f = ChangeAudioLanguage(fr,to)\n","        print(\"Audio generated\")\n","        return jsonify({'message': 'File uploaded successfully', 'file_path': file_path})\n","    except Exception as e:\n","        print(f\"Error during file upload: {e}\")\n","        return jsonify({'message': 'Error uploading file', 'error': str(e)}), 500\n","@app.route('/check_status')\n","def status():\n","    print(flag)\n","    return jsonify({\"ready\":flag})\n","\n","@app.route('/generate_audio')\n","def generate_audio():\n","        audio_path = \"./uploads/converted.wav\"\n","        print(audio_path)\n","        return send_file(audio_path, mimetype=\"audio/wav\")\n","\n","if __name__ == '__main__':\n","    app.run(debug=True)\n"]}]}